%% Group Information%Group Members:Dylan Au, Jaden Cajthaml, Tri Pham%Section:087%% Flappy Blockfunction mainCode=FlappyBlock()close all; clear all; clc;%Creating the Block        xB=[1,3,3,1,1]; yB=[8,8,10,10,8]; %Creating the pipes x=[16,21,21,16,16]; y=[0,0,20,20,0];%Creating the random gap between pipesyBot=randi([0,15]);yTop=yBot+5;Gap=[yBot,yBot,yTop,yTop,yBot];%variables that change when playingScore=0;highScore=[];trackScore=[];alive=1;input=0;%Initial game menu on startupchoice=questdlg('Flappy Block','Prompting User','Start Game','Exit Game','Exit Game');switch choice    case 'Start Game'    case 'Exit Game'    returnend%Figure Window Propertiesfigure('name','Flappy Block','KeyPressFcn',@Key_Up);whitebg('c')while alive if yB(1:2)<=0 |(xB(2:3)>=x([1,5]) & yB(1:2)<=Gap(4:5))|(xB(2:3)>=x([1,5]) & yB(3:4)>=Gap(3:4))     alive=0; fileID=fopen('score.txt','a'); fprintf(fileID,'%.0f\n',Score); fclose(fileID); trackScore=importdata('score.txt'); highScore=max([trackScore].'); message2=sprintf('High Score:%.0f%d',highScore); message=sprintf('You Died!\n Score:%.0f%d',Score);     msgbox({message,message2},'Scoreboard');     pause(2)     close all     breakend if input==1       yB=yB+1;   else        yB=yB-1/12;end   if input==1        input=0; end  if x(2)>1       x=x-0.12;       else     x=[15,20,20,15,15];    y=[0,0,20,20,0];    yBot=randi([0,15]);    yTop=yBot+5;    Gap=[yBot,yBot,yTop,yTop,yBot];end    if xB(4:5)>x(2:3)    Score=Score+1;end       formatSpec='Score: %.0f%d';    tally=sprintf(formatSpec,Score);    text=uicontrol('Style','text','Position',[450,350,55,15],'String',tally);    set(text,'backgroundcolor','c');        fill(x,y,'g',x,Gap,'c',xB,yB,'y','EdgeColor','none');     axis([0,20,0,20]);     set(gca,'YTickLabel',[],'XTickLabel',[])   pause(1/60); endfunction [] =  Key_Up(src,event)      user_var=event.Key;      if strcmp(user_var,'uparrow')==1     input = 1;end      endfunction [] = Key_Down(~,~)endend